USE Cinema;
DROP TABLE IF EXISTS Movie;

CREATE TABLE Movie (
	Movie_code 		VARCHAR(9) 		PRIMARY KEY,
	Director 		VARCHAR(50),
	Release_date	DATE 			NOT NULL,
	Age_limit		VARCHAR(3)		,
	Rating 			DECIMAL(2,1)	CHECK (Rating > 0),
	Time_limit 		DECIMAL(2,1)	CHECK (Time_limit > 0),
	Name 			VARCHAR(100)	NOT NULL
);

